import './App.css';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import React, { useState } from 'react'

function App() {

  const [bodyStyle, setBodyStyle] = useState ({
    color: '#242424',
    backgroundColor: '#fff'
})
const [btnText, setBtnText] = useState ("Enable Dark Mode")

const toogleStyle = () => {
    if(bodyStyle.color === '#242424') {
        setBodyStyle({
            color: '#fff',
            backgroundColor: '#242424'
        })
        setBtnText('Enable Light Mode')
    }
    else{
        setBodyStyle({
            color: '#242424',
            backgroundColor: '#fff'
        })
        setBtnText('Enable Dark Mode')
    }
}

  return (
      <>
      <div  style={bodyStyle}>
        <Navbar title= "Logo" />
        <TextForm heading="Text Area"/>
        <button className='btn btn-outline-warning ms-2' onClick={toogleStyle}>{btnText}</button>
        </div>
      </>
  );
}

export default App;
