import React, { useState } from 'react'
import PropTypes from 'prop-types'

function TextForm(props) {

    const [text, setText] = useState('Enter Text Here...')

    const [isUpperCase, setIsUpperCase] = useState(false)

    const handleConvert = () => {
        setIsUpperCase((prev) => !prev)
        setText((text) => (isUpperCase ? text.toLowerCase() : text.toUpperCase()))
    }

    const handleOnChange = (event) => {
        setText(event.target.value)
    }

    const handleSpeak = () => {
        let msg = new SpeechSynthesisUtterance();
        msg.text = text
        window.speechSynthesis.speak(msg)
    }

  return (
    <>
    <div className="container">
        <h4>{props.heading}</h4>
    <div className="mb-3 ">
    <textarea className="form-control" value={text} onChange={handleOnChange}  id="myBox" rows="8"></textarea>
    </div>
    <button className='btn btn-primary' onClick={handleConvert}> {isUpperCase ? 'Convert to Lowercase' : 'Convert to Uppercase'} </button>
    <button className='btn btn-warning ms-2' onClick={handleSpeak}>Read Text</button>

    <div className="my-3">
        <h1>Your text summary</h1>
        <p>{text.split(" ").length} words and {text.length} characters</p>
        <p>{0.008 * text.split(" ").length} Minutes read</p>
        <h2>PReview</h2>
        <p>{text}</p>
    </div>

    </div>
    </>
  )
}

export default TextForm

TextForm.propTypes = {
    heading: PropTypes.string.isRequired
}
