import React, { Component } from 'react'
import NewsItem from './NewsItem'

export default class News extends Component {


    constructor () {
        super();
        this.state = { 
            articles: [],
            loading: false
        }
    }

    async componentDidMount(){
        let url = "https://newsapi.org/v2/top-headlines?sources=bbc-news&apiKey=21473341dead4618bdcab7a417ae1e95";
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({
            articles: parsedData.articles
        })
    }

  render() {
    return (
        <div className='container '>
            <h2>News Monkey - Top Headlines</h2>
            <div className="row g-4">
                {this.state.articles.map((element)=>{               
                    return <div className="col-md-3" key={element.url}>
                        <NewsItem  title={element.title?element.title.slice(0,35): ""} description= {element.description?element.description.slice(0,88):""} imageUrl= {element.urlToImage} newsUrl={element.url} />
                    </div>
                })}
            </div>
      </div>
    )
  }
}
